import{_ as t,c as e,o as a,a4 as r}from"./chunks/framework.rIDq68an.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"02-Person/00-业务学习/02-跨境电商前置知识.md","filePath":"02-Person/00-业务学习/02-跨境电商前置知识.md"}'),d={name:"02-Person/00-业务学习/02-跨境电商前置知识.md"},l=r('<h2 id="供应链学习导航" tabindex="-1">供应链学习导航 <a class="header-anchor" href="#供应链学习导航" aria-label="Permalink to &quot;供应链学习导航&quot;">​</a></h2><p><a href="https://u1mc5cdv55.feishu.cn/base/YSPFbg7wIaj4RbsDjgicRYj6n1f?table=blkr01s9fXro8naw" target="_blank" rel="noreferrer">飞书云文档</a></p><p><a href="https://www.yuque.com/jiaowovitamin/uizu4s/npizln" target="_blank" rel="noreferrer">亚马逊FBA与FBM的业务介绍</a></p><h2 id="基础知识扫盲" tabindex="-1">基础知识扫盲 <a class="header-anchor" href="#基础知识扫盲" aria-label="Permalink to &quot;基础知识扫盲&quot;">​</a></h2><blockquote><p>亚马逊 卖家大学: <a href="https://gs.amazon.cn/learn" target="_blank" rel="noreferrer">点击跳转</a></p></blockquote><h2 id="销售平台" tabindex="-1">销售平台 <a class="header-anchor" href="#销售平台" aria-label="Permalink to &quot;销售平台&quot;">​</a></h2><blockquote><p>类似与国内的淘宝、京东、拼多多、得物等电商平台，在哪个平台进行销售</p></blockquote><ul><li>独立站 : <strong>自有电商销售平台</strong></li><li>Amazon : 类似于国内的淘宝, 将自己的商品放到淘宝上进行售卖. 这里在这个平台上又有很多种销售模式, 俗称与平台的合作模式,. 例如 <code>FBP</code> 、<code>FBM</code>、<code>DF</code>、<code>PO</code> 等</li><li>Ebay : 同Amazon</li><li>虾皮</li><li>速卖通</li><li>HD</li><li>Lazada</li><li>ManoMano</li></ul><h2 id="销售模式" tabindex="-1">销售模式 <a class="header-anchor" href="#销售模式" aria-label="Permalink to &quot;销售模式&quot;">​</a></h2><blockquote><p>在电商中 我们与销售平台之间的<code>合作关系</code></p></blockquote><table><thead><tr><th></th><th></th><th></th><th></th><th></th><th></th></tr></thead><tbody><tr><td>编码</td><td>模式</td><td>备注</td><td>货在哪</td><td>谁运营</td><td>谁发货</td></tr><tr><td>FBM</td><td>SC自发货</td><td>公司作为平台的卖家 <br>货在自有或者租赁的仓库里 <br>店铺自己发货 <br>(淘宝小店)</td><td>自有或三方仓</td><td>自运营</td><td>自发货</td></tr><tr><td>FBP<br><br>//FBA SC官方仓</td><td>SC官方仓</td><td>JD自营店, <br><br>公司作为京东的卖家 <br><br>货在京东的仓库里 <br><br>店铺自主运营,订单由官方发货</td><td>平台仓</td><td>自运营</td><td>平台发货</td></tr><tr><td>DF</td><td>VC自发货</td><td>平台供应商,<br><br>货在自有仓库或者租赁的仓库里 <br><br>由平台运营,我们发货</td><td>自有或三方仓</td><td>平台运营</td><td>自发货</td></tr><tr><td>PO</td><td>VC官方仓</td><td>平台供应商, <br><br>货在平台的仓库里 <br><br>由平台运营,平台发货</td><td>平台仓</td><td>平台运营</td><td>平台发货</td></tr></tbody></table><h2 id="术语扫盲" tabindex="-1">术语扫盲 <a class="header-anchor" href="#术语扫盲" aria-label="Permalink to &quot;术语扫盲&quot;">​</a></h2><ul><li>PV (PageView) 浏览曝光</li><li>UV (UserView) 用户曝光</li><li>CTR (Click Through Rate) 点击率</li><li>CVR (Click Value Rate) 转化率</li><li>GMV (Gross Merchandise volume) 电商网站成交总额</li><li>OMS (Order Manager System)</li><li>TMS (Transportation Manager System)</li><li>OFC (Order Fulfillment Center) 订单履约中心</li><li>WDC (Wholesale Distribution Center ) 批发配送中心</li><li>MAU (monthly active user) 月活跃用户</li></ul><h2 id="sku-问题" tabindex="-1">SKU 问题 <a class="header-anchor" href="#sku-问题" aria-label="Permalink to &quot;SKU 问题&quot;">​</a></h2><p><strong>为什么 SKU 只允许字母、数字和部分特殊字符</strong> ：</p><ul><li>很多公司会把 SKU 当做条形码来使用，即把 SKU 码打印成条形码（一维码），然后使用 PDA、扫码枪等设备进行扫描，用来解析条码并快速录入信息都按系统中</li><li>条形码的普遍编码方式都是 Code 128，而 Code 128 的格式没有办法将中文转成条码。</li></ul><h2 id="物流和快递的区别" tabindex="-1">物流和快递的区别 <a class="header-anchor" href="#物流和快递的区别" aria-label="Permalink to &quot;物流和快递的区别&quot;">​</a></h2><blockquote><p>快递： 突出一个快，主要以函件（文件、资料、图纸等）、货样、礼品、个人用品等为主的货物快递。 物流： 其中包含了快递，拿海运举例，海运的最小单位为集装箱，服务商会把多家客户，不同批次的货物塞到同一个集装箱里进行发货（拼箱操作），当然如果货物可以用掉以整个集装箱，也会整箱发货（整箱发货）</p></blockquote>',18),o=[l];function i(n,h,s,c,b,u){return a(),e("div",null,o)}const m=t(d,[["render",i]]);export{p as __pageData,m as default};
