import{_ as t,c as e,o as a,a4 as r}from"./chunks/framework.BG61BEI0.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"02-Person/00-业务学习/02-跨境电商前置知识.md","filePath":"02-Person/00-业务学习/02-跨境电商前置知识.md","lastUpdated":1715394924000}'),o={name:"02-Person/00-业务学习/02-跨境电商前置知识.md"},l=r('<h2 id="供应链学习导航" tabindex="-1">供应链学习导航 <a class="header-anchor" href="#供应链学习导航" aria-label="Permalink to &quot;供应链学习导航&quot;">​</a></h2><p><a href="https://u1mc5cdv55.feishu.cn/base/YSPFbg7wIaj4RbsDjgicRYj6n1f?table=blkr01s9fXro8naw" target="_blank" rel="noreferrer">飞书云文档</a></p><p><a href="https://www.yuque.com/jiaowovitamin/uizu4s/npizln" target="_blank" rel="noreferrer">亚马逊FBA与FBM的业务介绍</a></p><h2 id="基础知识扫盲" tabindex="-1">基础知识扫盲 <a class="header-anchor" href="#基础知识扫盲" aria-label="Permalink to &quot;基础知识扫盲&quot;">​</a></h2><blockquote><p>亚马逊 卖家大学: <a href="https://gs.amazon.cn/learn" target="_blank" rel="noreferrer">点击跳转</a></p></blockquote><h2 id="销售平台" tabindex="-1">销售平台 <a class="header-anchor" href="#销售平台" aria-label="Permalink to &quot;销售平台&quot;">​</a></h2><ul><li>独立站 : <strong>自有电商销售平台</strong></li><li>Amazon : 类似于国内的淘宝, 将自己的商品放到淘宝上进行售卖. 这里在这个平台上又有很多种销售模式, 俗称与平台的合作模式,. 例如 <code>FBP</code> 、<code>FBM</code>、<code>DF</code>、<code>PO</code> 等</li><li>Ebay : 同Amazon</li><li>HD</li><li>Lazada</li><li>ManoMano</li></ul><h2 id="销售模式" tabindex="-1">销售模式 <a class="header-anchor" href="#销售模式" aria-label="Permalink to &quot;销售模式&quot;">​</a></h2><blockquote><p>在电商中 我们与销售平台之间的<code>合作关系</code></p></blockquote><table><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td>编码</td><td>模式</td><td>备注</td></tr><tr><td>FBM</td><td>SC自发货</td><td>公司作为平台的卖家 <br>货在自有或者租赁的仓库里 <br>店铺自己发货 <br>(淘宝小店)</td></tr><tr><td>FBP<br><br>//FBA SC官方仓</td><td>SC官方仓</td><td>JD自营店, <br>公司作为平台的卖家 <br>货在平台的仓库里 <br>店铺自主运营,订单由官方发货</td></tr><tr><td>DF</td><td>VC自发货</td><td>平台供应商, <br>货在自有仓库或者租赁的仓库里 <br>由平台运营,我们发货</td></tr><tr><td>PO</td><td>VC官方仓</td><td>平台供应商, <br>货在平台的仓库里 <br>由平台运营,平台发货</td></tr></tbody></table><h2 id="库存扫盲" tabindex="-1">库存扫盲 <a class="header-anchor" href="#库存扫盲" aria-label="Permalink to &quot;库存扫盲&quot;">​</a></h2><p>跨境电商出口 占比市场百分之 70 的美国来说, 他那边的销售高峰的时间刚好是我们的休息时间 一旦有并发就会超卖 (库存侧痛点)</p><p>SC 业务按照仓库库存 DF 业务按照市场库存 官方仓平台按照平台的专享库存</p><p>仓库库存锁库存的业务逻辑是不准确的</p><p>销售库存和实物库存没有关系</p><blockquote><p>节点依赖过多 订单拆单换单、配仓、换仓、上传海外仓都会影响到销售库存的计算</p></blockquote><p>SC ：拉单锁市场库存，erp 配仓之后回调锁仓维度库存换仓重锁。上传海外仓通知释放，订单取消通知释放 DF： 拉单锁仓维度库存 官仓 ： 委托给平台做，平台处理。平台的专享库存 PO ：B 2 b Amazon 向 Vevor 批发。按照市场范围锁库存</p><h2 id="术语扫盲" tabindex="-1">术语扫盲 <a class="header-anchor" href="#术语扫盲" aria-label="Permalink to &quot;术语扫盲&quot;">​</a></h2><ul><li>PV (PageView) 浏览曝光</li><li>UV (UserView) 用户曝光</li><li>CTR (Click Through Rate) 点击率</li><li>CVR (Click Value Rate) 转化率</li><li>GMV (Gross Merchandise volume) 电商网站成交总额</li><li>OFC (Order Fulfillment Center) 订单履约中心</li><li>WDC (Wholesale Distribution Center ) 批发配送中心</li><li>MAU (monthly active user) 月活跃用户</li></ul>',19),i=[l];function d(n,c,h,s,b,_){return a(),e("div",null,i)}const m=t(o,[["render",d]]);export{p as __pageData,m as default};
